INSERT INTO `cxtj_home_page_log` VALUES ('241', 'home_page_tpl', '92', 'css', '/* 模板29 */\r\n\r\n.mainModule-29-content {\r\n    margin: 0 10px;\r\n}\r\n.mainModule-29-content .leftimg{\r\n	width: 139px;\r\n    height: 79px;\r\n    margin-right: 10px;\r\n    overflow: hidden;\r\n}\r\n.mainModule-29-title {\r\n    position: relative;\r\n    background-image: url(../images/mainModule-10/title.png);\r\n    padding-top: 11px;\r\n    padding-bottom: 8px;\r\n    padding-left: 68px;\r\n    background-size: 100% 100%;\r\n    background-repeat: no-repeat;\r\n}\r\n\r\n.mainModule-29-title a {\r\n    position: absolute;\r\n    right: 0;\r\n    display: inline-block;\r\n    padding-top: 11px;\r\n    padding-bottom: 8px;\r\n}\r\n\r\n.mainModule-29-list img {\r\n    width: 100%;\r\n    height:100%;\r\n    transition: all .5s ease;\r\n}\r\n.mainModule-29-list li:hover .leftimg img{\r\n    transform: scale(1.1);\r\n}\r\n\r\n.mainModule-29-list .content {\r\n    width: 231px;\r\n}\r\n\r\n.mainModule-29-list li {\r\n    margin-top: 12px;\r\n}\r\n\r\n.mainModule-29-list li:first-child {\r\n    margin-top: 20px;\r\n}\r\n\r\n.mainModule-29-list li:last-child {\r\n    margin-bottom: 3px;\r\n}\r\n\r\n.mainModule-29-list li h3 {\r\n    display: inline-block;\r\n    max-width: 224px;\r\n    max-width: 222px\\0;\r\n    max-width: 222px\\9;\r\n    white-space: nowrap;\r\n    overflow: hidden;\r\n    margin-top: 11px;\r\n    border-bottom: 1px solid transparent;\r\n}\r\n\r\n\r\n.mainModule-29-list li .introduce {\r\n    margin-top: 18px;\r\n}\r\n\r\n.mainModule-29-list li:hover h3 {\r\n    color: #162C6A;\r\n    border-bottom: 1px solid #162C6A;\r\n}\r\n\r\n.mainModule-29-list li .introduce span:first-child {\r\n    display: inline-block;\r\n    width: 115px;\r\n    white-space: nowrap;\r\n    overflow: hidden;\r\n    margin-right: 20px;\r\n}\r\n', '1', '1', '1528452522', '1528452522');
