round-size: 100% 100%;\r\n    background-repeat: no-repeat;\r\n}\r\n\r\n.mainModule-30-title a {\r\n    position: absolute;\r\n    right: 0;\r\n    display: inline-block;\r\n    padding-top: 11px;\r\n    padding-bottom: 8px;\r\n}\r\n.mainModule-30 .leftCon {\r\n    position: relative;\r\n    height: 214px;\r\n    margin-top: 20px;\r\n    overflow: hidden;\r\n}\r\n\r\n.mainModule-30 .leftCon img {\r\n    width: 100%;\r\n    height: 100%;\r\n    transition: all .5s ease;\r\n}\r\n.mainModule-30 .leftCon img:hover{\r\n    transform: scale(1.1);\r\n}\r\n\r\n.mainModule-30 .leftCon h3 {\r\n    position: absolute;\r\n    bottom: 0;\r\n    left: 0;\r\n    width: 342px;\r\n    height: 40px;\r\n    overflow: hidden;\r\n    padding: 0 19px 0;\r\n    color: #fff;\r\n    line-height: 40px;\r\n    background-color: rgba(0,0,0,.5);\r\n}\r\n\r\n.mainModule-30 ul li {\r\n    list-style: disc inside;\r\n    line-height: 20px;\r\n    margin-top: 14px;\r\n     height: 20px;\r\n    overflow: hidden;\r\n    border-bottom: 1px solid transparent;\r\n}\r\n\r\n.mainModule-30 ul li:first-child {\r\n    margin-top: 18px;\r\n}', 'admin/20180615/a0f6693e4aceb811b7e9c3c21c068251.png', '', '5', '0', '1529378179', '0');
INSERT INTO `cxtj_home_page_tpl` VALUES ('96', '模板34', '&lt;div class=&quot;f-left w600 mainModule-34 tpl-{{tpl_id}}&quot; id=&quot;module-{{id}}&quot; style=&quot;{{block_css}}&quot;&gt;\r\n    &lt;div class=&quot;mainModule-34-content&quot;&gt;\r\n        &lt;div class=&quot;mainModule-34-title font26 themecolor&quot; style=&quot;{{header_css}}&quot;&gt;{{title}}&lt;a href=&quot;{{more_url}}&quot; class=&quot;font16 themecolor&quot; style=&quot;{{more_css}}&quot;&gt;MORE&gt;&gt;&lt;/a&gt;&lt;/div&gt;\r\n        &lt;ul class=&quot;mainModule-34-list&quot;&gt;\r\n            {{#each data}}\r\n            &lt;li class=&quot;clearfix&quot;&gt;\r\n                &lt;a href=&quot;{{buildHomePageUrl url ../more_url}}&quot;&gt;\r\n                    &lt;img src=&quot;{{thumb}}&quot; alt=&quot;&quot; class=&quot;f-left&quot;&gt;\r\n                    &lt;div class=&quot;f-left content&quot;&gt;\r\n                        &lt;h3 class=&quot;font16 titlecolor o1&quot;&gt;{{title}}&lt;/h3&gt;\r\n                        &lt;p class=&quot;font14 introductioncolor introduce&quot;&gt;{{name}}&lt;/p&gt;\r\n                        &lt;div class=&quot;btn f-right font14&quot;&gt;预约&lt;/div&gt;\r\n                    &lt;/div&gt;\r\n                &lt;/a&gt;\r\n            &lt;/li&gt;\r\n            {{/each}}\r\n        &lt;/ul&gt;\r\n    &lt;/div&gt;\r\n&lt;/div&gt;', '/* 模板34 */\r\n\r\n    .mainModule-34-content {\r\n        margin: 0 10px;\r\n        height:505px;\r\n    }\r\n\r\n    .mainModule-34-title {\r\n        position: relative;\r\n        background-image: url(../images/mainModule-9/title.png);\r\n        padding-top: 0;\r\n        padding-bottom: 20px;\r\n        padding-left: 49px;\r\n        background-size: 100% 100%;\r\n        background-repeat: no-repeat;\r\n    }\r\n\r\n    .mainModule-34-title a {\r\n        position: absolute;\r\n        right: 0;\r\n        display: inline-block;\r\n        padding-top: 11px;\r\n        padding-bottom: 8px;\r\n    }\r\n\r\n