INSERT INTO `cxtj_home_page_tpl` VALUES ('97', '模板33', '&lt;div class=&quot;f-left w600 mainModule-33 tpl-{{tpl_id}}&quot; id=&quot;module-{{id}}&quot; style=&quot;{{block_css}}&quot;&gt;\r\n        &lt;div class=&quot;mainModule-33-content&quot;&gt;\r\n            &lt;div class=&quot;mainModule-33-title font26 themecolor&quot; style=&quot;{{header_css}}&quot;&gt;{{title}}&lt;a href=&quot;{{more_url}}&quot; class=&quot;font16 themecolor&quot; target=&quot;{{more_target}}&quot; style=&quot;{{more_css}}&quot;&gt;MORE&gt;&gt;&lt;/a&gt;&lt;/div&gt;\r\n            &lt;ul class=&quot;list&quot;&gt;\r\n                {{#each data}}\r\n                &lt;li class=&quot;clearfix&quot;&gt;&lt;a href=&quot;{{buildHomePageUrl url ../more_url}}&quot; class=&quot;titlecolor&quot;&gt;\r\n                    {{#if is_top}}\r\n                    &lt;span class=&quot;font14 themecolor f-left&quot;&gt;置顶&lt;/span&gt;\r\n                    {{/if}}\r\n                    &lt;strong  class=&quot; font16 f-left o1&quot;&gt;{{title}}&lt;/strong&gt;&lt;em class=&quot;font14 timecolor f-right&quot;&gt;[{{monthDay}}]&lt;/em&gt;&lt;/a&gt;&lt;/li&gt;\r\n                {{/each}}\r\n            &lt;/ul&gt;\r\n        &lt;/div&gt;\r\n    &lt;/div&gt;', '/* 模板33 */\r\n\r\n.mainModule-33 {\r\n    margin-top: 27px;\r\n    height:323px;\r\n}\r\n\r\n.mainModule-33-content {\r\n    margin: 0 10px;\r\n}\r\n\r\n.mainModule-33-title {\r\n    position: relative;\r\n    background-image: url(../images/mainModule-9/title.png);\r\n    background-repeat:no-repeat;\r\n    padding-top: 11px;\r\n    padding-bottom: 8px;\r\n    padding-left: 68px;\r\n    background-size: 100% 100%;\r\n}\r\n\r\n.mainModule-33-title a {\r\n    position: absolute;\r\n    right: 0;\r\n    display: inline-block;\r\n    padding-top: 11px;\r\n    padding-bottom: 8px;\r\n}\r\n\r\n.mainModule-33-content .list li {\r\n    width: 100%;\r\n    line-height: 35px;\r\n}\r\n\r\n.mainModule-33-content .list li:last-child {\r\n    border-bottom: 0;\r\n}\r\n\r\n.mainModule-33-content .list li a:hover {\r\n    color: #246CC1;\r\n}\r\n\r\n.mainModule-33-content .list li span {\r\n    border: 1px solid #246CC1;\r\n    height: 14px;\r\n    line-height: 14px;\r\n    padding: 2px 4px;\r\n    border-radius: 3px;\r\n    margin-right: 15px;\r\n    margin-top: 10px;\r\n}\r\n\r\n.mainModule-33-content .list li strong {\r\n    display: inline-block;\r\n    width: 431px;\r\n    white-space: nowrap;\r\n    overflow: hidden;\r\n}\r\n', 'admin/20180712/8069d0489f42128761401fc0a836acfd.png', '', '8', '0', '1531385743', '0');
INSERT INTO `cxtj_home_page_tpl` VALUES ('98', '模板7', '&lt;div class=&quot;f-left w1200 mainModule-7 tpl-{{tpl_id}}&quot; id=&quot;module-{{id}}&quot; style=&quot;{{block_css}}&quot;&gt;\r\n        &lt;div class=&quot;mainModule-7-content&quot;&gt;\r\n            &lt;div class=&quot;mainModule-7-title font26 themecolor&quot; style=&quot;{{header_css}}&quot;&gt;\r\n            {{title}}\r\n            &lt;a class=&quot;font16 themecolor&quot; href=&quot;{{more_url}}&quot; target=&quot;{{more_target}}&quot; style=&quot;{{more_css}}&quot;&gt;更多&gt;&gt;&lt;/a&gt;\r\n            &lt;/div&gt;\r\n            &lt;div class=&quot;mainModule-7-list&quot;&gt;\r\n            {{#each data}}\r\n            {{#compare \'lt\' @index 1}}\r\n                &lt;div class=&quot;list-left f-left&quot;&gt;\r\n                        &lt;a href=&quot;{{buildHomePageUrl url ../more_url}}&quot;&gt;\r\n                            &lt;img class=&quot;thumb&quot; src=&quot;{{thumb}}&quot; alt=&quot;&quot;&gt;\r\n                            &lt;div class=&quot;listLeft-jianjie&quot;&gt;\r\n                                &lt;h3 class=&quot;ellipsis&quot;&gt;{{title}}&lt;/h3&gt;\r\n                                &lt;p class=&quot;jianjie&quot;&gt;{{abstract}}&lt;/p&gt;\r\n                                &lt;p class=&quot;time&quot;&gt;{{time}}&lt;/p&gt;\r\n                            &lt;/div&gt;\r\n                            {{#if is_top}}\r\n                            &lt;img class=&quot;zhiding1&quot; src=&quot;/themes/simpleboot3/public/assets/whgcms/images/zhiding1.png&quot; alt=&quot;&quot;&gt;\r\n                        	{{/if}}\r\n                        &lt;/a&gt;\r\n                &lt;/div&gt;\r\n                {{/compare}}\r\n              {{/each}}\r\n                &lt;ul class=&quot;list-right f-right&quot; id=&quot;list-right&quot;&gt;\r\n                {{#each data}}\r\n            {{#compare \'gt\' @index 0}}\r\n                    &lt;li&gt;\r\n                        &